
from discord.ext import commands




class pi(commands.Cog, name="pi"):

    def __init__(self, client: commands.Bot) -> None:
        self.client = client

    @commands.Cog.listener()
    async def on_message(self, message):
        if not message.channel.id ==986617609652940840:
            return
        if "파이" in message.content or "pi" in message.content or "파이값" in message.content or "PI" in message.content or "Pi" in message.content or "∏" in message.content or "π" in message.content:
            await message.channel.send("3.")
            await message.channel.send("")
async def setup(client: commands.Bot) -> None:
    await client.add_cog(
        pi(client),
    )
